/* global VARIANT */

const { WebAssembly } = global
const decodeBase64 = require('../functions/decode-base64')

const RIPEMD160_WASM_BASE64 = ''
let SHA1_WASM_BASE64 = null
let SHA256_WASM_BASE64 = null

if (typeof VARIANT === 'undefined' || VARIANT === 'browser') {
  SHA1_WASM_BASE64 = ''
  SHA256_WASM_BASE64 = ''
}

const WASM_PAGE_SIZE = 65536
const RESERVED_PAGES = 2 * 3
const MAX_USER_MEMORY = 32 * 1024 * 1024
const HASH_PAGES = RESERVED_PAGES + MAX_USER_MEMORY / WASM_PAGE_SIZE

let memory = null
let memoryBuffer = null

let ripemd160 = null
let sha1 = null
let sha256 = null

const getMemory = () => { memory = memory || new WebAssembly.Memory({ initial: HASH_PAGES }); return memory }
const checkAvailableMemory = (size) => { if (size > MAX_USER_MEMORY) throw new Error(`data too big: ${size}`) }
const getMemoryBuffer = () => { memoryBuffer = memoryBuffer || new Uint8Array(getMemory().buffer); return memoryBuffer }
const getRipemd160 = () => { ripemd160 = ripemd160 || compileInstance(RIPEMD160_WASM_BASE64).exports.ripemd160; return ripemd160 }
const getSha1 = () => { sha1 = sha1 || compileInstance(SHA1_WASM_BASE64).exports.sha1; return sha1 }
const getSha256 = () => { sha256 = sha256 || compileInstance(SHA256_WASM_BASE64).exports.sha256; return sha256 }

function compileInstance (base64) {
  const bytes = decodeBase64(base64)
  const module = new WebAssembly.Module(bytes)
  const imports = { env: { memory: getMemory() } }
  const instance = new WebAssembly.Instance(module, imports)
  return instance
}

module.exports = {
  checkAvailableMemory,
  getMemoryBuffer,
  getRipemd160,
  getSha1,
  getSha256
}
