const { WebAssembly } = global
const decodeBase64 = require('../functions/decode-base64')

const BN_WASM_BASE64 = ''
const BN2_WASM_BASE64 = ''
const SECP256K1_WASM_BASE64 = ''
const ECDSA_WASM_BASE64 = ''

const BN_LEN = 64
const DIGIT_SIZE = 4
const BN_SIZE = BN_LEN * DIGIT_SIZE
const PT_SIZE = BN_LEN * 2 * DIGIT_SIZE

const WASM_PAGE_SIZE = 65536
const MEMORY_PER_WASM = 1310720
const SECP256K1_INIT_DATA = BN_SIZE * 40000
const MEMORY_PAGES = Math.ceil((MEMORY_PER_WASM * 4 + SECP256K1_INIT_DATA) / WASM_PAGE_SIZE)

let memory = null
let memoryBuffer = null

let bnInstance = null
let bn2Instance = null
let secp256k1Instance = null
let ecdsaInstance = null

let startPosition = null
let nPos = null

const getMemory = () => { memory = memory || new WebAssembly.Memory({ initial: MEMORY_PAGES }); return memory }
const getMemoryBuffer = () => { memoryBuffer = memoryBuffer || new Uint8Array(getMemory().buffer); return memoryBuffer }

const getNPos = () => { getSecp256k1Exports(); return nPos }

const getBnExports = () => {
  if (!bnInstance) {
    const bytes = decodeBase64(BN_WASM_BASE64)
    const module = new WebAssembly.Module(bytes)
    const imports = { env: { memory: getMemory() } }
    bnInstance = new WebAssembly.Instance(module, imports)
  }
  return bnInstance.exports
}

const getBn2Exports = () => {
  if (!bn2Instance) {
    const bytes = decodeBase64(BN2_WASM_BASE64)
    const module = new WebAssembly.Module(bytes)
    const imports = { env: { memory: getMemory() } }
    Object.assign(imports.env, getBnExports())
    bn2Instance = new WebAssembly.Instance(module, imports)
  }
  return bn2Instance.exports
}

const getSecp256k1Exports = () => {
  if (!secp256k1Instance) {
    const bytes = decodeBase64(SECP256K1_WASM_BASE64)
    const module = new WebAssembly.Module(bytes)
    const imports = { env: { memory: getMemory() } }
    Object.assign(imports.env, getBnExports(), getBn2Exports())
    secp256k1Instance = new WebAssembly.Instance(module, imports)

    const memory = getMemoryBuffer()
    startPosition = memory.length - SECP256K1_INIT_DATA / 2
    secp256k1Instance.exports.secp256k1_init(startPosition)
    nPos = startPosition + BN_SIZE
  }
  return secp256k1Instance.exports
}

const getEcdsaExports = () => {
  if (!ecdsaInstance) {
    const bytes = decodeBase64(ECDSA_WASM_BASE64)
    const module = new WebAssembly.Module(bytes)
    const imports = { env: { memory: getMemory() } }
    Object.assign(imports.env, getBnExports(), getBn2Exports(), getSecp256k1Exports())
    ecdsaInstance = new WebAssembly.Instance(module, imports)
    ecdsaInstance.exports.ecdsa_init(startPosition)
  }
  return ecdsaInstance.exports
}

function writeBN (memory, pos, buffer) {
// Our digits are stored on the WASM side in reverse order, and each digit also in little endian,
// so we simply can write each byte in reverse order.
  const padding = (DIGIT_SIZE - buffer.length % DIGIT_SIZE) % DIGIT_SIZE
  const length = Math.max(buffer.length + padding, DIGIT_SIZE)
  let bytePos = pos + length - 1
  for (let i = 0; i < padding; i++) { memory[bytePos--] = 0 }
  buffer.forEach(byte => { memory[bytePos--] = byte })
  if (buffer.length === 0) memory[pos] = memory[pos + 1] = memory[pos + 2] = memory[pos + 3] = 0
  memory[pos + BN_SIZE - 1] = 0
  memory[pos + BN_SIZE - 2] = 0
  memory[pos + BN_SIZE - 3] = 0
  memory[pos + BN_SIZE - 4] = 0
  memory[pos + BN_SIZE - 5] = 0
  memory[pos + BN_SIZE - 6] = 0
  memory[pos + BN_SIZE - 7] = 0
  memory[pos + BN_SIZE - 8] = length / DIGIT_SIZE
  getBnExports().bn_unpad(pos)
}

function readBN (memory, pos) {
  let size = memory[pos + BN_SIZE - 8] * DIGIT_SIZE
  for (let i = pos + size - 1; i >= pos; i--) {
    if (memory[i]) break
    size--
  }
  const buffer = new Uint8Array(size)
  let bytePos = buffer.length - 1
  let memoryPos = pos
  for (let i = 0; i < size; i++) {
    buffer[bytePos--] = memory[memoryPos++]
  }
  return buffer
}

module.exports = {
  BN_SIZE,
  PT_SIZE,
  getMemory,
  getMemoryBuffer,
  getNPos,
  getBnExports,
  getBn2Exports,
  getSecp256k1Exports,
  getEcdsaExports,
  writeBN,
  readBN
}
